public class Employees{
	def static log;
	def name;
	def salary;
	def dept;

	public void printEmployee(){
		log.info"Employee name is: $name";
	}

	public def getSalary(def amount){
		salary += amount;
		return salary;
	}

	public def add(a,b,c=0,d=9){
		return a+b+c+d;
	}
}

